package com.hsicen.letcode.array/** * 作者：hsicen  8/4/21 14:38 * 邮箱：codinghuang@163.com * 作用： * 描述：寻找数组中第一个缺失的正数 * * 给你一个未排序的整数数组 nums ，请你找出其中没有出现的最小的正整数。 * 请你实现时间复杂度为 O(n) 并且只使用常数级别额外空间的解决方案。 */fun firstMissingPositive(nums: IntArray): Int {    nums.sort()    var ret = -1    for (num in nums) {        if (ret > 0 && num != ret) {            return ret        }        if (num > 0) {            ret = num + 1        }    }    return nums.size}fun main() {    val src = intArrayOf(1, 2, 0)    println(firstMissingPositive(src))    println(firstMissingPositive(intArrayOf(3, 4, -1, 1)))    println(firstMissingPositive(intArrayOf(7, 8, 9, 11, 12)))}