package com.hsicen.letcode.array/** * 作者：hsicen  8/12/21 17:33 * 邮箱：codinghuang@163.com * 作用： * 描述：Remove Element * Given an array nums and a value val, remove all instances of that value in-place and return the new length. * Do not allocate extra space for another array, you must do this by modifying the input array in-place with O(1) extra memory. * The order of elements can be changed. It doesn't matter what you leave beyond the new length. */fun removeElement(nums: IntArray, `val`: Int): Int {    var newLen = 0    nums.forEach {        if (`val` != it) {            nums[newLen] = it            newLen++        }    }    return newLen}fun main() {    println(removeElement(intArrayOf(0, 1, 2, 2, 3, 0, 4, 2), 2))}