package com.hsicen.letcode.linklist.practice/** * 作者：hsicen  8/3/21 16:31 * 邮箱：codinghuang@163.com * 作用： * 描述：双向链表 */class DoubleLinkList(    val data: Int,    var pre: DoubleLinkList? = null,    var next: DoubleLinkList? = null)private fun add(list: DoubleLinkList?, data: Int): DoubleLinkList {    val new = DoubleLinkList(data)    return list?.let {        new.next = it.next        new.pre = it        it.next = new        list    } ?: new}private fun delete(list: DoubleLinkList?, data: Int): DoubleLinkList? {    return list}