package com.hsicen.letcode.nowcoderimport java.util.*/** * 作者：hsicen  9/24/21 09:50 * 邮箱：codinghuang@163.com * 作用： * 描述：HJ2 计算某字母出现次数 */fun main() {  val sc = Scanner(System.`in`)  val strs = sc.nextLine()  val le = sc.nextLine()  println(letterCount(strs, le))}fun letterCount(str: String, letter: String): Int {  val map = HashMap<String, Int>()  for (c in str) {    val s = c.lowercase()    map[s] = map[s]?.let { it + 1 } ?: 1  }  return map[letter.lowercase()] ?: 0}