package com.hsicen.letcode.search/** * 作者：hsicen  8/18/21 09:26 * 邮箱：codinghuang@163.com * 作用： * 描述：搜索旋转排序数组 II * 使用二分查找法在数组中寻找指定元素 */private fun search(nums: IntArray, target: Int): Boolean {    if (nums.isEmpty()) return false    var low = 0    var high = nums.size - 1    while (low <= high) {        val mid = low + (high - low).shr(1)        if (mid == target) return true        when {            // 无法判断那边有序            nums[low] == nums[mid] && nums[mid] == nums[high] -> {                low++                high--            }            // 左边有序            nums[low] <= nums[mid] -> {                // 目标在有序区间                if (nums[low] <= target && target < nums[mid]) {                    high = mid - 1                } else {                    low = mid + 1                }            }            else -> {                if (nums[mid] < target && target <= nums[high]) {                    low = mid + 1                } else {                    high = mid - 1                }            }        }    }    return false}