package com.hsicen.letcode.stack.practice/** * 作者：hsicen  8/11/21 09:24 * 邮箱：codinghuang@163.com * 作用： * 描述：build a stack with array */class StackArray(private var capacity: Int) {    private var mItems = IntArray(capacity) { -1 }    private var size = 0    fun push(data: Int) {        if (size >= capacity) {            capacity *= 2            mItems = mItems.copyOf(capacity)        }        mItems[size++] = data    }    fun pop(): Int {        if (0 == size) return -1        return mItems[--size]    }    fun peek(): Int {        if (0 == size) return -1        return mItems[size - 1]    }    fun empty(): Boolean {        return 0 == size    }    fun print() {        println(mItems.contentToString())    }}fun main() {    val stack = StackArray(10)    stack.push(1)    stack.push(3)    stack.push(5)    stack.push(7)    stack.push(9)    stack.print()    stack.pop()    stack.pop()    stack.pop()    stack.pop()    stack.pop()    stack.push(10)    stack.print()}