package com.hsicen.lib.array;import java.util.stream.IntStream;/** * 作者：hsicen  8/2/21 17:49 * 邮箱：codinghuang@163.com * 作用： * 描述：支持动态扩容的数组 */public class KIntArray {    private static final float threadHold = 0.8f;    private int[] items;    private int capacity;    private int size = 0;    public KIntArray(int capacity) {        this.capacity = capacity;        items = new int[capacity];    }    public void add(int item) {        if (size >= items.length) {            capacity += capacity * threadHold;            int[] tmp = new int[capacity];            copyToNew(tmp);        }        items[size++] = item;    }    public boolean delete(int item) {        for (int i = 0; i < items.length; i++) {            if (items[i] == item) {                items[i] = -1;                size--;                return true;            }        }        return false;    }    public int delete2(int pos) {        if (pos > size) return -1;        int tmp = items[pos];        items[pos] = -1;        return tmp;    }    private void copyToNew(int[] tmp) {        IntStream.range(0, items.length).forEach(i -> tmp[i] = items[i]);        items = tmp;    }    public int size() {        return size;    }}