package com.hsicen.lib.array/** * 作者：hsicen  8/12/21 18:00 * 邮箱：codinghuang@163.com * 作用： * 描述：斐波那契数 * Given an array of integers A sorted in non-decreasing order, * return an array of the squares of each number, also in sorted non-decreasing order. */fun fib(n: Int): Int {    return when (n) {        0, 1 -> n        else -> fib(n - 1) + fib(n - 2)    }}fun main() {    println(fib(5))    println(fib(7))    println(fib(9))}