package com.hsicen.lib.linklist.practice/** * 作者：hsicen  8/3/21 16:20 * 邮箱：codinghuang@163.com * 作用： * 描述：循环链表的实现 */class CircleList(val data: Int, var next: CircleList? = null)private fun add(list: CircleList?, data: Int): CircleList {    val new = CircleList(data)    return if (null == list) {        new.next = new        new    } else {        new.next = list.next        list.next = new        list    }}// 考虑链表中存在重复元素的删除情况， 关键是找到循环截止条件private fun delete(list: CircleList?, data: Int): CircleList? {    return list}