package com.hsicen.lib.nowcoder/** * 作者：hsicen  9/24/21 10:23 * 邮箱：codinghuang@163.com * 作用： * 描述：DataStructure */private class ListNode(var `val`: Int) {  var next: ListNode? = null}private fun ReverseList(head: ListNode?): ListNode? {  // write code here  if (head?.next == null) return head  val newHead = ListNode(-1)  var p = head  while (p != null) {    val next = p.next    p.next = newHead.next    newHead.next = p    p = next  }  return newHead.next}fun main() {  val node1 = ListNode(1)  val node2 = ListNode(2)  val node3 = ListNode(3)  val node4 = ListNode(4)  val node5 = ListNode(5)  node1.next = node2  node2.next = node3  print(node1.`val`)  print(node1.next?.`val`)  print(node1.next?.next?.`val`)  val newHead = ReverseList(node1)  println()  print(newHead?.`val`)  print(newHead?.next?.`val`)  print(newHead?.next?.next?.`val`)}