package com.hsicen.lib.queue.practice/** * 作者：hsicen  8/13/21 11:02 * 邮箱：codinghuang@163.com * 作用： * 描述：use array to build a queue. * feature: first in and first out. */class ArrayQueue(val capacity: Int) {    private val mItems = IntArray(capacity) { -1 }    private var head = 0    private var tail = 0    // push item into queue    fun push(data: Int) {        if (tail == capacity) {            if (0 == head) return            for (i in head until tail) {                mItems[i - head] = mItems[i]            }            tail -= head            head = 0        }        mItems[tail++] = data    }    fun pop(): Int {        if (empty()) return -1        return mItems[head++]    }    fun peek(): Int {        if (empty()) return -1        return mItems[head]    }    fun size() = tail - head    fun empty() = tail == head    fun full() = tail == capacity && 0 == head}