package com.hsicen.lib.search/** * 作者：hsicen  8/19/21 15:41 * 邮箱：codinghuang@163.com * 作用： * 描述：最长回文子串 * 给你一个字符串 s，找到 s 中最长的回文子串。 */private fun longestPalindrome(s: String): String {    if (s.length < 2) return s    var start = 0    var end = 0    (1..s.length / 2).forEach { first ->        for (pos in 1..first) {            if (first == 1) {                if (s[first] == s[first - 1]) {                    start = first - 1                    end = first                }                return@forEach            } else if (s[first - pos] == s[first + pos]) {                start = first - pos                end = first + pos            } else if (s[first] == s[first - pos]) {                start = first - pos                end = first            } else if (s[first] == s[first + pos]) {                start = first                end = first + pos            } else break        }    }    return s.substring(start, end + 1)}fun main() {    println(longestPalindrome("cbbd"))}