package com.hsicen.lib.stackimport java.util.*/** * 作者：hsicen  8/20/21 14:33 * 邮箱：codinghuang@163.com * 作用： * 描述：表达式求值 */private fun evalRPN(tokens: Array<String>): Int {    val nums = Stack<Int>()    val operator = listOf("+", "-", "*", "/")    for (char in tokens) {        if (char in operator) {            val b = nums.pop()            val a = nums.pop()            val c = when (char) {                "+" -> a + b                "-" -> a - b                "*" -> a * b                "/" -> a / b                else -> 0            }            nums.push(c)        } else {            nums.push(char.toInt())        }    }    return nums.peek()}fun main() {    println(evalRPN(arrayOf("2", "1", "+", "3", "*")))    println(evalRPN(arrayOf("4", "13", "5", "/", "+")))    println(evalRPN(arrayOf("10", "6", "9", "3", "+", "-11", "*", "/", "*", "17", "+", "5", "+")))}