package com.hsicen.lib.stackimport java.util.*/** * 作者：hsicen  8/18/21 15:42 * 邮箱：codinghuang@163.com * 作用： * 描述：有效的括号 */private fun isValid(s: String): Boolean {    val len = s.length    if (0 == len) return true    if (0 != len % 2) return false    val bracket = Stack<Char>()    val sample = listOf('(', '[', '{')    var tmp: Char    for (indexChar in s) {        when (indexChar) {            in sample -> bracket.push(indexChar)            else -> {                if (bracket.isEmpty()) return false                tmp = bracket.pop()                when (indexChar) {                    ')' -> if (tmp != '(') return false                    ']' -> if (tmp != '[') return false                    '}' -> if (tmp != '{') return false                }            }        }    }    return bracket.isEmpty()}private fun isValid2(s: String): Boolean {    val len = s.length    if (0 == len) return true    if (0 != len % 2) return false    var str = s    while ("{}" in str || "()" in str || "[]" in str) {        str = str.replace("{}", "")        str = str.replace("[]", "")        str = str.replace("()", "")    }    return str.isEmpty()}fun main() {    println(isValid2("[{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[()]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}]"))    println(isValid2("[{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[([{((({([{{[([[[[[[({{[()]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}])]}})]]]]]])]}}])})))}]"))}