package com.hsicen.lib.stack.practiceimport java.util.*/** * 作者：hsicen  8/12/21 15:30 * 邮箱：codinghuang@163.com * 作用： * 描述：achieve a browser with stack. * 1.can go forward and back. * 2.can go another site. */class Browser {    private val forwardStack = Stack<String>()    private val backStack = Stack<String>()    // open a new site    fun open(site: String) {        backStack.push(site)        forwardStack.clear()    }    fun goBack() {        if (canBack()) {            val site = backStack.pop()            forwardStack.push(site)        }    }    fun goForward() {        if (canForward()) {            val site = forwardStack.pop()            backStack.push(site)        }    }    // check current site    fun checkPage() {        println("current site: ${backStack.peek()}")    }    private fun canForward() = !forwardStack.empty()    private fun canBack() = !backStack.empty()}