package com.hsicen.lib.stack.practiceimport com.hsicen.lib.linklist.practice.LinkList/** * 作者：hsicen  8/12/21 15:18 * 邮箱：codinghuang@163.com * 作用： * 描述：build a stack with linked list. */class StackLinked {    private val head = LinkList(-1)    fun push(data: Int) {        val newNode = LinkList(data, head.next)        head.next = newNode    }    fun pop(): Int {        if (empty()) return -1        val delete = head.next        head.next = delete?.next        return delete?.data ?: -1    }    fun peek(): Int {        if (empty()) return -1        return head.next?.data ?: -1    }    fun empty(): Boolean {        return null == head.next    }    override fun toString(): String {        return head.next.toString()    }}fun main() {    val linkStack = StackLinked()    linkStack.push(1)    linkStack.push(3)    linkStack.push(5)    linkStack.push(7)    println(linkStack)    println(linkStack.pop())    println(linkStack.pop())    println(linkStack.pop())    println(linkStack)}